    public void remoteDataReceiver() {
        if (EmailProcessor.getIfSubjAndAttachRemotelySet()) {

            emailSubjectField.setText(EmailProcessor.getEmailSubject());
            emailBodyField.setText(EmailProcessor.getEmailText());
            if (EmailProcessor.attachmentIsMandatory) {
                emailAttachmentTxtField.setText(ReportProcessor.getBuiltXLSPath().substring(0, 15) + "...");
            }
        }

    }

    private void emailAttachmentChooseBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_emailAttachmentChooseBtnActionPerformed

        int chosen = emailAttachmentChooser.showOpenDialog(null);

        Integer selected = 0;
        String uploadedFilesCounterTxt;
        if (chosen == JFileChooser.APPROVE_OPTION) {

            File[] selectedFiles = emailAttachmentChooser.getSelectedFiles();
            for (File x : selectedFiles) {
                selected++;
            }

            if (selected == 1) {
                uploadedFilesCounterTxt = "Izvēlēts " + selected + " fails";
            } else {
                uploadedFilesCounterTxt = "Izvēlēti " + selected + " faili";
            }

            emailAttachmentTxtField.setText(uploadedFilesCounterTxt);

            EmailProcessor.setAttachmentIsMandatory(true);

        }

    }//GEN-LAST:event_emailAttachmentChooseBtnActionPerformed

