    private void emailSenderFieldActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_emailSenderFieldActionPerformed

        if (EmailFormValidated()) {

            EmailProcessor.setEmailReceiver(emailReceiverField.getText());

            String testtx = emailBodyField.getText();

            EmailProcessor.setEmailText(emailBodyField.getText());

    //        if (!EmailProcessor.getIfSubjAndAttachRemotelySet()) {

                if (EmailProcessor.attachmentIsMandatory) {

                    File[] selectedFiles = emailAttachmentChooser.getSelectedFiles();

                    ArrayList<String> midlist = new ArrayList<>();
                    for (File file : selectedFiles) {
                        midlist.add(file.getAbsolutePath());
                    }
                    String[] finalAttachmentPathList = new String[midlist.size()];
                    int counter = 0;
                    for (String x : midlist) {
                        finalAttachmentPathList[counter] = x;
                        counter++;

                    }

                    EmailProcessor.setEmailAttachmentDirectory(finalAttachmentPathList);
                }
                EmailProcessor.setEmailSubject(emailSubjectField.getText());
    //        }

            EmailProcessor.GenerateAndSendEmail();
            EmailProcessor.setIfSubjAndAttachRemotelySet(false);
            EmailProcessor.setAttachmentIsMandatory(false);
        }

    }//GEN-LAST:event_emailSenderFieldActionPerformed

